// Featherlight lightbox functionality - minimal implementation

(function($) {
    $.fn.featherlight = function(options) {
        options = options || {};

        return this.each(function() {
            const $this = $(this);

            $this.on('click', function(e) {
                e.preventDefault();

                const src = this.href || this.src;
                if (src) {
                    openLightbox(src, options);
                }
            });
        });
    };

    function openLightbox(src, options) {
        // Create lightbox HTML
        const lightbox = document.createElement('div');
        lightbox.className = 'featherlight';
        lightbox.innerHTML = `
            <div class="featherlight-content">
                <button class="featherlight-close-icon" type="button">&times;</button>
                <img src="${src}" alt="Lightbox image">
            </div>
        `;

        document.body.appendChild(lightbox);
        lightbox.style.display = 'block';

        // Close functionality
        lightbox.addEventListener('click', function(e) {
            if (e.target === lightbox || e.target.className === 'featherlight-close-icon') {
                document.body.removeChild(lightbox);
            }
        });

        // Escape key to close
        document.addEventListener('keydown', function(e) {
            if (e.key === 'Escape') {
                if (document.body.contains(lightbox)) {
                    document.body.removeChild(lightbox);
                }
            }
        });
    }
})(window.$ || window.jQuery);