// Waypoints minimal implementation for scroll-triggered events

(function() {
    window.Waypoint = function(options) {
        this.element = options.element;
        this.handler = options.handler;
        this.offset = options.offset || 0;

        this.init();
    };

    window.Waypoint.prototype.init = function() {
        const self = this;
        window.addEventListener('scroll', function() {
            const elementTop = self.element.getBoundingClientRect().top;
            const elementBottom = self.element.getBoundingClientRect().bottom;

            if (elementTop <= window.innerHeight && elementBottom >= 0) {
                self.handler.call(self.element);
            }
        });
    };
})();